package mypkg;

public class Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i, j, n=1;
	        for(i=0; i<5; i++)
	        {
	            for(j=0; j<=i; j++)
	            {
	                System.out.print(n+ " ");
	                n++;
	            }
	            System.out.println();
	        }

	}

}
